import React, { Component } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit } from '@fortawesome/free-solid-svg-icons'
import { Modal, Form, Input } from 'antd';

export default class Skills extends Component {

    constructor(props) {
        super(props)
        this.state = {
            visible: false,
            layout: {
                labelCol: {
                    span: 8,
                },
                wrapperCol: {
                    span: 16,
                },
            },
            tailLayout: {
                wrapperCol: {
                    offset: 8,
                    span: 16,
                },
            },
        }
    }

    handleOk = (e) => {
        this.setState({
            visible: false,
        });
    };

    handleCancel = (e)=> {
        this.setState({
            visible: false,
        });
    };

    onFinish = (values) => {
        // console.log('Success:', values);
    };

    onFinishFailed = errorInfo => {
    };

    editRow = (e) => {
        this.setState({
            visible: true,
        });
    }
    render() {
        return (
            <React.Fragment>
                <Modal title="Edit Skill" visible={this.state.visible} onOk={this.handleOk} onCancel={this.handleCancel}>
                    <Form {...this.state.layout} name="basic" initialValues={{ remember: true, }} onFinish={this.onFinish()} onFinishFailed={this.onFinishFailed()} >
                        <Form.Item label="Skill Name" name="Skill Name" rules={[{ required: true, message: 'Please input Skill Name!', },]}>
                            <Input />
                        </Form.Item>
                        <Form.Item label="Level" name="Level" rules={[{ required: true, message: 'Please input Level!', },]}>
                            <Input />
                        </Form.Item>
                        <Form.Item label="Experience" name="Experience" rules={[{ required: true, message: 'Please input Experience!', },]}>
                            <Input />
                        </Form.Item>
                        <Form.Item {...this.state.tailLayout}>
                        </Form.Item>
                    </Form>
                </Modal>
                <div className="Box col-6">
                    <div className="Box-v3-header">
                        <h1>My Technical Skills</h1>
                    </div>
                    <div className="Box-v3-content Content">
                        <table className="table mts-table">
                            <thead>
                                <tr>
                                    <th>Skill Name</th><th>Level</th>
                                    <th style={{ width: '150px' }}>Experience</th>
                                    <th>Edit</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Angular</td>
                                    <td>xxx</td>
                                    <td style={{ width: '150px' }}>
                                        <div style={{ display: 'inline-flex' }}>
                                            <span>1 year</span>&nbsp;
                                            <span>12 month<span>s</span></span>
                                        </div>
                                    </td>
                                    <td>
                                        <div className="mts-edit edit-resume-btn" onClick={this.editRow}>
                                            <FontAwesomeIcon icon={faEdit} />
                                        </div>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
