import React, { Suspense } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  BrowserRouter as Router,
  Switch,
  Route, Redirect
} from "react-router-dom";
// const SignInComponent = React.lazy(() => import('./views/loginManager/sign-in/signIn'));
// const DashboardComponent = React.lazy(() => import('./views/dashboard/dashboard'));
import DashboardComponent from './views/layout/layout'
import SignInComponent from './views/loginManager/sign-in/signIn'
const axios = require('axios').default;
axios.defaults.baseURL = 'http://localhost:3000';
axios.defaults.headers.post['Content-Type'] = 'application/x-www-form-urlencoded';

function App() {
  return (
      <Router>
        <div className="Layout">
          <Switch>
            <Route path="/web/dashboard" component={DashboardComponent}>
              {/* <DashboardComponent /> */}
            </Route>
            <Route exact path="/" component={SignInComponent}>
              {/* <SignInComponent /> */}
            </Route>
          </Switch>
        </div>
      </Router>
  );
}

export default App;
