import React from 'react';
import './signIn.css';
import {Redirect, Link} from 'react-router-dom';
const axios = require('axios').default;
axios.defaults.baseURL = 'http://localhost:3000';
axios.defaults.headers.post['Content-Type'] = 'application/x-www-form-urlencoded';



export default class SignIn extends React.Component {
    constructor(props) {
        super(props);
        console.log(props)
        this.state = {
            email: '',
            password: ''
        };
        this.handleChangePassword = this.handleChangePassword.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        // axios.get('/users')
        //     .then((result) => {
        //         if (result) {
        //             this.setState({
        //                 isLoaded: true,
        //                 items: result
        //             });
        //         }
        //     })
        //     .catch(function (error) {
        //         console.log(error);
        //     })
        //     .finally(function () {
        //         // always executed
        //     });
    }

    handleChangeEmail = (event) => {
        this.setState({ email: event.target.value });
    }

    handleChangePassword(event) {
        this.setState({ password: event.target.value });
    }

    handleSubmit = (event) => {
        // axios.post('/users/login', { email: this.state.email, password: this.state.password })
        //     .then((result) => {
        //         if (result) {
        //             this.setState({
        //                 isLoaded: true,
        //                 information: result
        //             });
        //             localStorage.setItem('userinformation', JSON.stringify(result));
        //             return  <Redirect  to="/web/dashboard" />
        //         }
        //     })
        //     .catch(function (error) {
        //         console.log(error);
        //     })
        //     .finally(function () {
        //     });
        // event.preventDefault();
        console.log(this.props);
        return  <Redirect push from='/'  to="/web/dashboard" />
    }

    render() {
        return (
            <div className="Container">
                <div className="Content">
                    <div className="title">
                        <h1>Sign In</h1>
                    </div>
                    <form >
                        <div className="group-control mx-2 mt-65">
                            <label htmlFor="uname">Username <span>*</span></label>
                            <input type="text" placeholder="Enter Username" name="uname" value={this.state.email} onChange={this.handleChangeEmail} />
                        </div>
                        <div className="group-control mb-4 mx-2">
                            <label htmlFor="psw">Password <span>*</span></label>
                            <input type="password" placeholder="Enter Password" name="psw" value={this.state.password} onChange={this.handleChangePassword} ></input>
                        </div>
                        <div className="group-control mx-2">
                            <button type="button" onClick={this.handleSubmit}>Login</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}
